// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract HealthRecord {
    // Enum for user roles
    enum Role { Patient, Doctor, Admin }

    // Struct for user details
    struct User {
        string name;
        address userAddress;
        Role role;
        bool isActive;
        string ipfsHash;
    }

    // State variables
    address public owner;
    address public constant DEFAULT_ADMIN = 0xD9073E73717FCA172f29B55A0368ae41De35D237;
    mapping(address => User) public users;
    mapping(address => mapping(address => bool)) public doctorAccess;

    // Events
    event UserAdded(address indexed userAddress, string name, Role role);
    event UserUpdated(address indexed userAddress, string name, Role role);
    event UserDeleted(address indexed userAddress);
    event AccessGranted(address indexed patient, address indexed doctor);
    event AccessRevoked(address indexed patient, address indexed doctor);

    // Modifiers
    modifier onlyAdmin() {
        require(users[msg.sender].role == Role.Admin, "Only admin can perform this action");
        require(users[msg.sender].isActive, "Admin account is not active");
        _;
    }

    modifier onlyDoctor() {
        require(users[msg.sender].role == Role.Doctor, "Only doctor can perform this action");
        require(users[msg.sender].isActive, "Doctor account is not active");
        _;
    }

    modifier onlyPatient() {
        require(users[msg.sender].role == Role.Patient, "Only patient can perform this action");
        require(users[msg.sender].isActive, "Patient account is not active");
        _;
    }

    // Constructor
    constructor() {
        owner = msg.sender;
        // Add contract deployer as admin if it's the default admin address
        if (msg.sender == DEFAULT_ADMIN) {
            users[msg.sender] = User("Default Admin", msg.sender, Role.Admin, true, "");
            emit UserAdded(msg.sender, "Default Admin", Role.Admin);
        }
    }

    // Admin functions
    function addUser(
        address _userAddress,
        string memory _name,
        Role _role,
        string memory _ipfsHash
    ) public onlyAdmin {
        require(_userAddress != address(0), "Invalid address");
        require(!users[_userAddress].isActive, "User already exists");

        users[_userAddress] = User(_name, _userAddress, _role, true, _ipfsHash);
        emit UserAdded(_userAddress, _name, _role);
    }

    function updateUser(
        address _userAddress,
        string memory _name,
        string memory _ipfsHash
    ) public onlyAdmin {
        require(users[_userAddress].isActive, "User does not exist");

        users[_userAddress].name = _name;
        users[_userAddress].ipfsHash = _ipfsHash;
        emit UserUpdated(_userAddress, _name, users[_userAddress].role);
    }

    function deleteUser(address _userAddress) public onlyAdmin {
        require(users[_userAddress].isActive, "User does not exist");
        require(_userAddress != DEFAULT_ADMIN, "Cannot delete default admin");
        users[_userAddress].isActive = false;
        emit UserDeleted(_userAddress);
    }

    // Patient functions
    function grantAccessToDoctor(address _doctorAddress) public onlyPatient {
        require(users[_doctorAddress].role == Role.Doctor, "Invalid doctor address");
        require(users[_doctorAddress].isActive, "Doctor is not active");

        doctorAccess[msg.sender][_doctorAddress] = true;
        emit AccessGranted(msg.sender, _doctorAddress);
    }

    function revokeAccessFromDoctor(address _doctorAddress) public onlyPatient {
        doctorAccess[msg.sender][_doctorAddress] = false;
        emit AccessRevoked(msg.sender, _doctorAddress);
    }

    // View functions
    function getUserRole(address _userAddress) public view returns (Role) {
        return users[_userAddress].role;
    }

    function checkDoctorAccess(address _patientAddress, address _doctorAddress) 
        public 
        view 
        returns (bool) 
    {
        return doctorAccess[_patientAddress][_doctorAddress];
    }

    // Emergency admin function
    function addDefaultAdmin() public {
        require(msg.sender == DEFAULT_ADMIN, "Only default admin can call this");
        require(!users[DEFAULT_ADMIN].isActive, "Default admin already exists");

        users[DEFAULT_ADMIN] = User("Default Admin", DEFAULT_ADMIN, Role.Admin, true, "");
        emit UserAdded(DEFAULT_ADMIN, "Default Admin", Role.Admin);
    }
}
