// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract HealthRecord {
    // Enum for user roles
    enum Role { Patient, Doctor, Admin }

    // Struct for user details
    struct User {
        string name;
        address userAddress;
        Role role;
        bool isActive;
        string ipfsHash;
    }

    // State variables
    address public owner;
    address public constant DEFAULT_ADMIN = 0xD9073E73717FCA172f29B55A0368ae41De35D237;
    mapping(address => User) public users;
    mapping(address => mapping(address => bool)) public doctorAccess;
    mapping(address => address[]) public authorizedDoctorsList;
    mapping(address => mapping(address => bool)) public accessRequests;
    mapping(address => address[]) public pendingRequests;

    // Events
    event UserAdded(address indexed userAddress, string name, Role role);
    event UserUpdated(address indexed userAddress, string name, Role role);
    event UserDeleted(address indexed userAddress);
    event AccessGranted(address indexed patient, address indexed doctor);
    event AccessRevoked(address indexed patient, address indexed doctor);
    event AccessRequested(address indexed patient, address indexed doctor);
    event RequestRejected(address indexed patient, address indexed doctor);

    // Modifiers
    modifier onlyAdmin() {
        require(users[msg.sender].role == Role.Admin, "Only admin can perform this action");
        require(users[msg.sender].isActive, "Admin account is not active");
        _;
    }

    modifier onlyDoctor() {
        require(users[msg.sender].role == Role.Doctor, "Only doctor can perform this action");
        require(users[msg.sender].isActive, "Doctor account is not active");
        _;
    }

    modifier onlyPatient() {
        require(users[msg.sender].role == Role.Patient, "Only patient can perform this action");
        require(users[msg.sender].isActive, "Patient account is not active");
        _;
    }

    // Constructor
    constructor() {
        owner = msg.sender;
        // Add contract deployer as admin if it's the default admin address
        if (msg.sender == DEFAULT_ADMIN) {
            users[msg.sender] = User("Default Admin", msg.sender, Role.Admin, true, "");
            emit UserAdded(msg.sender, "Default Admin", Role.Admin);
        }
    }

    // Admin functions
    function addUser(
        address _userAddress,
        string memory _name,
        Role _role,
        string memory _ipfsHash
    ) public onlyAdmin {
        require(_userAddress != address(0), "Invalid address");

        // Always clean up existing data, whether the user exists or not
        if (_role == Role.Patient) {
            // Get all authorized doctors (if any)
            address[] memory doctors = authorizedDoctorsList[_userAddress];

            // Clear all doctor access mappings
            for (uint i = 0; i < doctors.length; i++) {
                delete doctorAccess[_userAddress][doctors[i]];
            }

            // Clear authorized doctors list
            delete authorizedDoctorsList[_userAddress];

            // Clear pending requests
            for (uint i = 0; i < pendingRequests[_userAddress].length; i++) {
                delete accessRequests[_userAddress][pendingRequests[_userAddress][i]];
            }
            delete pendingRequests[_userAddress];
        } else if (_role == Role.Doctor) {
            // Clean up any existing doctor access data
            for (uint i = 0; i < authorizedDoctorsList[_userAddress].length; i++) {
                address patient = authorizedDoctorsList[_userAddress][i];
                delete doctorAccess[patient][_userAddress];
            }
            delete authorizedDoctorsList[_userAddress];
        }

        // Create or update user
        users[_userAddress] = User(_name, _userAddress, _role, true, _ipfsHash);
        emit UserAdded(_userAddress, _name, _role);
    }

    function updateUser(
        address _userAddress,
        string memory _name,
        string memory _ipfsHash
    ) public onlyAdmin {
        require(users[_userAddress].isActive, "User does not exist");

        users[_userAddress].name = _name;
        users[_userAddress].ipfsHash = _ipfsHash;
        emit UserUpdated(_userAddress, _name, users[_userAddress].role);
    }

    function deleteUser(address _userAddress) public onlyAdmin {
        require(users[_userAddress].isActive, "User does not exist");
        require(_userAddress != DEFAULT_ADMIN, "Cannot delete default admin");

        // Clean up associated data based on role
        if (users[_userAddress].role == Role.Patient) {
            // Get list of authorized doctors
            address[] memory authorizedDoctors = authorizedDoctorsList[_userAddress];

            // Clear all doctor access mappings explicitly
            for (uint i = 0; i < authorizedDoctors.length; i++) {
                address doctor = authorizedDoctors[i];
                delete doctorAccess[_userAddress][doctor];
                emit AccessRevoked(_userAddress, doctor);
            }

            // Clear authorized doctors list
            delete authorizedDoctorsList[_userAddress];

            // Clear pending requests and access requests
            for (uint i = 0; i < pendingRequests[_userAddress].length; i++) {
                address doctor = pendingRequests[_userAddress][i];
                delete accessRequests[_userAddress][doctor];
                emit RequestRejected(_userAddress, doctor);
            }
            delete pendingRequests[_userAddress];
        } else if (users[_userAddress].role == Role.Doctor) {
            // If deleting a doctor, remove their access to all patients
            // Get all patients this doctor has access to
            for (uint i = 0; i < authorizedDoctorsList[msg.sender].length; i++) {
                address patient = authorizedDoctorsList[msg.sender][i];
                delete doctorAccess[patient][_userAddress];
                emit AccessRevoked(patient, _userAddress);
            }
            delete authorizedDoctorsList[msg.sender];
        }

        // Mark user as inactive
        users[_userAddress].isActive = false;
        emit UserDeleted(_userAddress);
    }


    function requestAccess(address _patientAddress) public {
        require(users[msg.sender].role == Role.Doctor, "Only doctors can request access");
        require(users[msg.sender].isActive, "Doctor account is not active");
        require(users[_patientAddress].role == Role.Patient, "Invalid patient address");
        require(!doctorAccess[_patientAddress][msg.sender], "Access already granted");
        require(!accessRequests[_patientAddress][msg.sender], "Request already pending");

        accessRequests[_patientAddress][msg.sender] = true;
        pendingRequests[_patientAddress].push(msg.sender);
        emit AccessRequested(_patientAddress, msg.sender);
    }

    function getPendingRequests(address _patientAddress) public view returns (address[] memory) {
        return pendingRequests[_patientAddress];
    }

    // Patient functions
    function grantAccessToDoctor(address _doctorAddress) public onlyPatient {
        require(users[_doctorAddress].role == Role.Doctor, "Invalid doctor address");
        require(users[_doctorAddress].isActive, "Doctor is not active");

        doctorAccess[msg.sender][_doctorAddress] = true;
        // Add doctor to authorized list if not already present
        bool doctorExists = false;
        for(uint i = 0; i < authorizedDoctorsList[msg.sender].length; i++) {
            if(authorizedDoctorsList[msg.sender][i] == _doctorAddress) {
                doctorExists = true;
                break;
            }
        }
        if(!doctorExists) {
            authorizedDoctorsList[msg.sender].push(_doctorAddress);
        }

        // Remove from pending requests if exists
        if (accessRequests[msg.sender][_doctorAddress]) {
            accessRequests[msg.sender][_doctorAddress] = false;
            for(uint i = 0; i < pendingRequests[msg.sender].length; i++) {
                if(pendingRequests[msg.sender][i] == _doctorAddress) {
                    pendingRequests[msg.sender][i] = pendingRequests[msg.sender][pendingRequests[msg.sender].length - 1];
                    pendingRequests[msg.sender].pop();
                    break;
                }
            }
        }

        emit AccessGranted(msg.sender, _doctorAddress);
    }

    function revokeAccessFromDoctor(address _doctorAddress) public onlyPatient {
        doctorAccess[msg.sender][_doctorAddress] = false;
        // Remove doctor from authorized list
        for(uint i = 0; i < authorizedDoctorsList[msg.sender].length; i++) {
            if(authorizedDoctorsList[msg.sender][i] == _doctorAddress) {
                // Move the last element to this position and pop the last element
                authorizedDoctorsList[msg.sender][i] = authorizedDoctorsList[msg.sender][authorizedDoctorsList[msg.sender].length - 1];
                authorizedDoctorsList[msg.sender].pop();
                break;
            }
        }
        emit AccessRevoked(msg.sender, _doctorAddress);
    }

    function rejectAccessRequest(address _doctorAddress) public onlyPatient {
        require(users[_doctorAddress].role == Role.Doctor, "Invalid doctor address");
        require(accessRequests[msg.sender][_doctorAddress], "No pending request from this doctor");

        // Clear the access request
        accessRequests[msg.sender][_doctorAddress] = false;

        // Remove from pending requests
        for(uint i = 0; i < pendingRequests[msg.sender].length; i++) {
            if(pendingRequests[msg.sender][i] == _doctorAddress) {
                pendingRequests[msg.sender][i] = pendingRequests[msg.sender][pendingRequests[msg.sender].length - 1];
                pendingRequests[msg.sender].pop();
                break;
            }
        }

        emit RequestRejected(msg.sender, _doctorAddress);
    }

    // View functions
    function getUserRole(address _userAddress) public view returns (Role) {
        return users[_userAddress].role;
    }

    function checkDoctorAccess(address _patientAddress, address _doctorAddress) 
        public 
        view 
        returns (bool) 
    {
        return doctorAccess[_patientAddress][_doctorAddress];
    }

    function getAuthorizedDoctors(address _patientAddress) public view returns (address[] memory) {
        return authorizedDoctorsList[_patientAddress];
    }

    // Emergency admin function
    function addDefaultAdmin() public {
        require(msg.sender == DEFAULT_ADMIN, "Only default admin can call this");
        require(!users[DEFAULT_ADMIN].isActive, "Default admin already exists");

        users[DEFAULT_ADMIN] = User("Default Admin", DEFAULT_ADMIN, Role.Admin, true, "");
        emit UserAdded(DEFAULT_ADMIN, "Default Admin", Role.Admin);
    }
}
